namespace LeetCode.CSharp;

public class FindTheDifferenceC
{
    public void Test()
    {
        var s =
            "";
        var t =
            "";
        var res = FindTheDifference(s, t);

        Console.WriteLine($"Different char is {res.ToString()}");
    }

    private char FindTheDifference(string s, string t)
    {
        if (string.IsNullOrWhiteSpace(s))
            return t[0];

        var dic1 = BuildHashSet(s);
        var dic2 = BuildHashSet(t);
        var res = FindDiffFromHashSet(dic1, dic2);
        return res;
    }

    private Dictionary<char, int> BuildHashSet(string s)
    {
        var res = new Dictionary<char, int>();
        foreach (var c in s.ToCharArray())
        {
            if (res.TryGetValue(c, out var count))
            {
                res[c] = ++count;
            }
            else
            {
                res[c] = 1;
            }
        }

        return res;
    }

    private char FindDiffFromHashSet(Dictionary<char, int> hs1, Dictionary<char, int> hs2)
    {
        foreach (var hs in hs2)
            if (!hs1.TryGetValue(hs.Key, out var hs2Val) ||
                hs.Value != hs2Val)
                return hs.Key;

        return new char();
    }
}